// src/pages/ProjectDetail.js
import React, { useState, useEffect } from "react";
import api from "../api/api";

function ProjectDetail({ projectId, onBack }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("todo");
  const [assignee, setAssignee] = useState("");
  const [editingTask, setEditingTask] = useState(null);
  const [filterStatus, setFilterStatus] = useState("");
  const [filterAssignee, setFilterAssignee] = useState("");

  // Fetch tasks
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await api.get(`/tasks?projectId=${projectId}`);
        setTasks(res.data);
      } catch (err) {
        alert(err.response?.data?.message || "Failed to load tasks");
      }
    };
    fetchTasks();
  }, [projectId]);

  // Add or update task
  const saveTask = async () => {
    if (!title) return alert("Title required");
    try {
      if (editingTask) {
        const res = await api.put(`/tasks/${editingTask.id}`, {
          title,
          description,
          status,
          assignee,
        });
        setTasks(tasks.map(t => t.id === editingTask.id ? res.data : t));
        setEditingTask(null);
      } else {
        const res = await api.post("/tasks", {
          title,
          description,
          status,
          assignee,
          ProjectId: projectId,
        });
        setTasks([...tasks, res.data]);
      }
      setTitle("");
      setDescription("");
      setStatus("todo");
      setAssignee("");
    } catch (err) {
      alert(err.response?.data?.message || "Failed to save task");
    }
  };

  const editTask = (task) => {
    setEditingTask(task);
    setTitle(task.title);
    setDescription(task.description);
    setStatus(task.status);
    setAssignee(task.assignee || "");
  };

  const deleteTask = async (id) => {
    try {
      await api.delete(`/tasks/${id}`);
      setTasks(tasks.filter(t => t.id !== id));
    } catch (err) {
      alert(err.response?.data?.message || "Failed to delete task");
    }
  };

  // Filtered tasks
  const filteredTasks = tasks.filter(t => 
    (filterStatus ? t.status === filterStatus : true) &&
    (filterAssignee ? t.assignee === filterAssignee : true)
  );

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <button
        onClick={onBack}
        style={{
          marginBottom: "20px",
          padding: "5px 10px",
          borderRadius: "5px",
          border: "none",
          backgroundColor: "#FF4500",
          color: "white",
          cursor: "pointer",
        }}
      >
        Back to Dashboard
      </button>

      <h2 style={{ color: "#228B22" }}>Project Detail</h2>

      {/* Task Form */}
      <div style={{ marginBottom: "20px", marginTop: "20px" }}>
        <h3 style={{ color: "#4B0082" }}>{editingTask ? "Edit Task" : "Add Task"}</h3>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ marginRight: "10px", padding: "5px", borderRadius: "5px" }}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{ marginRight: "10px", padding: "5px", borderRadius: "5px" }}
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          style={{ marginRight: "10px", padding: "5px", borderRadius: "5px" }}
        >
          <option value="todo">Todo</option>
          <option value="in-progress">In Progress</option>
          <option value="done">Done</option>
        </select>
        <input
          type="text"
          placeholder="Assignee"
          value={assignee}
          onChange={(e) => setAssignee(e.target.value)}
          style={{ marginRight: "10px", padding: "5px", borderRadius: "5px" }}
        />
        <button
          onClick={saveTask}
          style={{
            backgroundColor: "#4B0082",
            color: "white",
            padding: "5px 15px",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          {editingTask ? "Save" : "Add"}
        </button>
      </div>

      {/* Filters */}
      <div style={{ marginBottom: "20px" }}>
        <h4>Filter Tasks</h4>
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          style={{ marginRight: "10px", padding: "5px", borderRadius: "5px" }}
        >
          <option value="">All Status</option>
          <option value="todo">Todo</option>
          <option value="in-progress">In Progress</option>
          <option value="done">Done</option>
        </select>
        <input
          type="text"
          placeholder="Assignee"
          value={filterAssignee}
          onChange={(e) => setFilterAssignee(e.target.value)}
          style={{ padding: "5px", borderRadius: "5px" }}
        />
      </div>

      {/* Task List */}
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {filteredTasks.map(task => (
          <li
            key={task.id}
            style={{
              marginBottom: "10px",
              padding: "10px",
              background: "#e6f2ff",
              borderRadius: "8px",
            }}
          >
            <strong>{task.title}</strong> ({task.status}) <br/>
            {task.description} <br/>
            <em>Assignee: {task.assignee || "Unassigned"}</em> <br/>
            <button
              onClick={() => editTask(task)}
              style={{
                backgroundColor: "#228B22",
                color: "white",
                marginRight: "5px",
                border: "none",
                borderRadius: "5px",
                padding: "5px 10px",
                cursor: "pointer",
              }}
            >
              Edit
            </button>
            <button
              onClick={() => deleteTask(task.id)}
              style={{
                backgroundColor: "#FF4500",
                color: "white",
                border: "none",
                borderRadius: "5px",
                padding: "5px 10px",
                cursor: "pointer",
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProjectDetail;
