import React, { useState } from "react";
import Dashboard from "./pages/Dashboard";
import Login from "./pages/Login";
import Register from "./pages/Register";

function App() {
  const [page, setPage] = useState("register"); // default to register page

  const handleLoginSuccess = () => setPage("dashboard");
  const handleRegisterSuccess = () => setPage("login");

  if (page === "register") return <Register onRegisterSuccess={handleRegisterSuccess} />;
  if (page === "login") return <Login onLogin={handleLoginSuccess} />;
  if (page === "dashboard") return <Dashboard />;

  return null;
}

export default App;
