import React, { useState, useEffect } from "react";
import api from "../api/api";

function Dashboard({ onLogout }) {
  const [projects, setProjects] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await api.get("/projects");
        setProjects(res.data);
      } catch (err) {
        alert(err.response?.data?.message || "Failed to load projects");
      }
    };
    fetchProjects();
  }, []);

  const addProject = async () => {
    if (!title) return alert("Title is required");
    setLoading(true);
    try {
      const res = await api.post("/projects", { name: title, description });
      setProjects([...projects, res.data]);
      setTitle("");
      setDescription("");
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add project");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Arial, sans-serif", background: "#f0f4f8", minHeight: "100vh" }}>

      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2 style={{ color: "#2c3e50", fontSize: "2rem" }}>Dashboard</h2>
        <button
          onClick={onLogout}
          style={{
            backgroundColor: "#e74c3c",
            color: "white",
            border: "none",
            padding: "8px 15px",
            borderRadius: "5px",
            cursor: "pointer",
            fontWeight: "bold"
          }}
        >
          Logout
        </button>
      </div>

      <div style={{
        background: "#fff",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
        margin: "30px 0"
      }}>
        <h3 style={{ color: "#4B0082" }}>Add Project</h3>
        <input
          type="text"
          placeholder="Project title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ marginRight: "10px", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{ marginRight: "10px", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
        />
        <button
          onClick={addProject}
          disabled={loading}
          style={{
            backgroundColor: "#3498db",
            color: "white",
            border: "none",
            padding: "8px 15px",
            borderRadius: "5px",
            cursor: loading ? "not-allowed" : "pointer",
            fontWeight: "bold"
          }}
        >
          {loading ? "Adding..." : "Add Project"}
        </button>
      </div>

      <div style={{
        background: "#fff",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)"
      }}>
        <h3 style={{ color: "#FF4500", marginBottom: "15px" }}>Projects</h3>
        <ul style={{ listStyle: "none", padding: 0 }}>
          {projects.map((project) => (
            <li key={project.id} style={{ padding: "10px", borderBottom: "1px solid #eee" }}>
              <strong style={{ color: "#2980b9" }}>{project.name}</strong>: {project.description}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
