import React, { useState, useEffect } from "react";
import api from "../api/api";

function Dashboard({ onLogout }) {
  const [projects, setProjects] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await api.get("/projects");
        setProjects(res.data);
      } catch (err) {
        alert(err.response?.data?.message || "Failed to load projects");
      }
    };
    fetchProjects();
  }, []);

  const addProject = async () => {
    if (!title) return alert("Title is required");
    setLoading(true);
    try {
      const res = await api.post("/projects", { name: title, description });
      setProjects([...projects, res.data]);
      setTitle("");
      setDescription("");
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add project");
    }
    setLoading(false);
  };

  const containerStyle = {
    fontFamily: "Arial, sans-serif",
    padding: "20px",
    backgroundColor: "#f0f4f8",
    minHeight: "100vh",
  };

  const buttonStyle = {
    backgroundColor: "#4B0082",
    color: "white",
    padding: "8px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "0.3s",
  };

  const cardStyle = {
    backgroundColor: "white",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
    marginBottom: "10px",
    transition: "0.3s",
  };

  const cardHover = {
    transform: "scale(1.02)",
    boxShadow: "0 6px 12px rgba(0,0,0,0.2)",
  };

  return (
    <div style={containerStyle}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2 style={{ color: "#228B22" }}>Dashboard</h2>
        <button
          onClick={onLogout}
          style={{ ...buttonStyle, backgroundColor: "#FF4500" }}
        >
          Logout
        </button>
      </div>

      <div style={{ marginTop: "30px", marginBottom: "20px" }}>
        <h3 style={{ color: "#4B0082", marginBottom: "10px" }}>Add Project</h3>
        <input
          type="text"
          placeholder="Project title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            marginRight: "10px",
          }}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{
            padding: "8px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            marginRight: "10px",
          }}
        />
        <button onClick={addProject} disabled={loading} style={buttonStyle}>
          {loading ? "Adding..." : "Add Project"}
        </button>
      </div>

      <h3 style={{ color: "#FF4500", marginBottom: "10px" }}>Projects</h3>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "15px" }}>
        {projects.map((project) => (
          <div
            key={project.id}
            style={cardStyle}
            onMouseEnter={(e) => Object.assign(e.currentTarget.style, cardHover)}
            onMouseLeave={(e) => Object.assign(e.currentTarget.style, cardStyle)}
          >
            <h4 style={{ color: "#4B0082", fontWeight: "bold", marginBottom: "5px" }}>
              {project.name}
            </h4>
            <p>{project.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
